# Define static workout dates
static_dates = [
  '2024-10-01', '2024-10-04', '2024-10-07', # Week 1
  '2024-10-10', '2024-10-13', '2024-10-16', # Week 2
  '2024-10-19', '2024-10-22', '2024-10-25', # Week 3
  '2024-10-28', '2024-11-01', '2024-11-04', # Week 4
  '2024-11-07', '2024-11-10', '2024-11-13', # Week 5
  '2024-11-16', '2024-11-19', '2024-11-22'  # Week 6
]

# Generate workouts, workout_exercises, and set_entries
static_dates.each_with_index do |date, index|
  users.each do |user|
    # Create a workout for each user
    workout = Workout.create!(user: user, date: date, notes: "Workout session #{index + 1} for #{user.name}")

    # Select 3-5 random exercises for the workout
    selected_exercises = exercises.first(10).sample(rand(3..5))

    selected_exercises.each_with_index do |exercise, ex_index|
      # Create a workout_exercise entry
      workout_exercise = WorkoutExercise.create!(workout: workout, exercise: exercise)

      # Generate 3-4 sets for each exercise
      (1..rand(3..4)).each do |set_number|
        # Weight calculation based on the revised logic
        base_weight = (exercise.api_key.to_s[-2..].to_i) + (index * 2) + ex_index * 5
        SetEntry.create!(
          workout_exercise: workout_exercise,
          set_number: set_number,
          reps: rand(8..12), # Random reps between 8 and 12
          weight: base_weight + set_number * 2 # Incremental weight for each set
        )
      end
    end
  end
end


puts "Static seed data with static dates created successfully!"